INSERT INTO `wp_posts` VALUES("515", "1", "2019-10-25 02:32:44", "2019-10-25 02:32:44", "<!-- wp:image {\"width\":526,\"height\":328} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"https://www.hostinger.vn/huong-dan/wp-content/uploads/sites/10/2018/09/javascript-la-gi.jpg\" alt=\"JavaScript là gì\" width=\"526\" height=\"328\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>JavaScript</strong>&nbsp;is the most popular programming language in the world for the past 20 years.&nbsp;It is also one of the three main languages ​​of web programming:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li><a rel=\"noreferrer noopener\" href=\"https://www.hostinger.vn/huong-dan/html-cheat-sheet-co-html5-tags-moi/\" target=\"_blank\">HTML</a>&nbsp;: Helps you add content to web pages.</li><li><a rel=\"noreferrer noopener\" href=\"https://www.hostinger.vn/huong-dan/khac-biet-giua-inline-external-va-internal-style-css\" target=\"_blank\">CSS</a>&nbsp;: Design format, layout, style, alignment of web pages.</li><li>JavaScript: Improve the way websites work.</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://blog.alvasky.com/wp-content/uploads/2019/03/javascript-illustration.png\" alt=\"Hình ảnh có liên quan\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li></li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>JavaScript can be&nbsp;<a rel=\"noreferrer noopener\" href=\"https://www.bitdegree.org/learn/javascript-basics/\" target=\"_blank\">learned quickly</a>&nbsp;and easily for a variety of purposes, from improving website functionality to running games and creating web-based software.&nbsp;Moreover, there are thousands of JavaScript templates and applications out there, thanks to the dedication of the community, especially Github.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://st.quantrimang.com/photos/image/2017/03/10/Javascript.jpg\" alt=\"Kết quả hình ảnh cho javascript là gì\"/></figure>\n<!-- /wp:image -->", "JavaScrip", "", "inherit", "closed", "closed", "", "385-revision-v1", "", "", "2019-10-25 02:32:44", "2019-10-25 02:32:44", "", "385", "https://localhost/softwarestorepnv/2019/10/25/385-revision-v1/", "0", "revision", "", "0");
